.dob-container {
  display: flex;
  flex-direction: column;
  margin-bottom: 1rem;
  position: relative;
}

.dob-label {
  font-family: 'Roboto', sans-serif;
  font-size: 14px;
  font-weight: 600;
  color: #333;
}

.required-star {
  color: #dc2626;
  font-size: 1rem;
  /* font-weight: 600; */
  /* margin-top: -12px; */
}

.input-container {
  position: relative;
  display: inline-block;
}

.dob-input {
  width: 100%;
  padding: 0.5rem 2.5rem 0.5rem 0.5rem;
  /* add right padding for icon */
  border: 1px solid #ccc;
  border-radius: 4px;
  font-size: 14px;
  color: #2d2d2d;
  font-family: sans-serif;
  margin-top: 10px;
  cursor: pointer;
  box-sizing: border-box;
}

.calendar-icon {
  position: absolute;
  right: 10px;
  top: 59%;
  transform: translateY(-50%);
  color: #888;
  pointer-events: none;
  /* ensures clicking icon triggers input */
  transition: color 0.2s ease;
}

.dob-input:focus+.calendar-icon {
  color: #007BFF;
}

.dob-input:focus {
  outline: none;
  border-color: #007BFF;
  box-shadow: 0 0 0 2px rgba(0, 123, 255, 0.25);
  background-color: #f0f8ff;
  transition: all 0.3s ease;
}

/* Calendar Overlay */
.calendar-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.3);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
}

.calendar-container {
  width: 320px;
  background: #ffffff;
  border-radius: 12px;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.2);
  padding: 20px;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
}

.calendar-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 20px;
}

.nav-btn {
  background: none;
  border: none;
  cursor: pointer;
  padding: 8px;
  border-radius: 6px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #666;
  transition: all 0.2s ease;
}

.nav-btn:hover {
  background-color: #f0f0f0;
  color: #333;
}

.month-year-selectors {
  display: flex;
  gap: 8px;
}

.month-select,
.year-select {
  border: none;
  background: none;
  font-size: 16px;
  font-weight: 500;
  cursor: pointer;
  padding: 4px 8px;
  border-radius: 4px;
  transition: background-color 0.2s ease;
}

.month-select:hover,
.year-select:hover {
  background-color: #f0f0f0;
}

.month-select:focus,
.year-select:focus {
  outline: none;
  background-color: #e0e0e0;
}

.calendar-weekdays {
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  gap: 2px;
  margin-bottom: 12px;
}

.weekday {
  text-align: center;
  font-size: 12px;
  font-weight: 500;
  color: #888;
  padding: 8px 4px;
}

.calendar-grid {
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  gap: 2px;
}

.calendar-day {
  aspect-ratio: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  border-radius: 8px;
  font-size: 14px;
  font-weight: 400;
  transition: all 0.2s ease;
  position: relative;
}

.calendar-day.current-month {
  color: #333;
}

.calendar-day.other-month {
  color: #ccc;
}

.calendar-day.current-month:hover {
  background-color: #f0f0f0;
}

.calendar-day.today {
  background-color: #ff6b35;
  color: white;
  font-weight: 500;
}

.calendar-day.selected {
  background-color: #ff6b35;
  color: white;
  font-weight: 500;
  border-radius: 8px;
}

.calendar-day.today.selected {
  background-color: #ff6b35;
  box-shadow: 0 0 0 2px #ff6b35, 0 0 0 4px rgba(255, 107, 53, 0.2);
}

.calendar-footer {
  display: flex;
  justify-content: space-between;
  margin-top: 20px;
  padding-top: 16px;
  border-top: 1px solid #e0e0e0;
}

.footer-btn {
  background: none;
  border: none;
  cursor: pointer;
  padding: 8px 16px;
  border-radius: 6px;
  font-size: 14px;
  font-weight: 500;
  transition: all 0.2s ease;
}

.today-btn {
  color: #ff6b35;
}

.today-btn:hover {
  background-color: rgba(255, 107, 53, 0.1);
}

.clear-btn {
  color: #666;
}

.clear-btn:hover {
  background-color: #f0f0f0;
}

.close-btn {
  color: #ff6b35;
}

.close-btn:hover {
  background-color: rgba(255, 107, 53, 0.1);
}

/* Responsive adjustments */
@media (max-width: 480px) {
  .calendar-overlay {
    padding: 20px;
  }

  .calendar-container {
    width: 100%;
    max-width: 320px;
  }
}

