import { NgStyle } from '@angular/common';
import { Component, ElementRef, HostBinding, Input } from '@angular/core';

@Component({
    selector: 'orms-tooltip',
    templateUrl: './tooltip.component.html',
    styleUrl: './tooltip.component.css',
    imports: [NgStyle]
})
export class TooltipComponent {
  @Input() text = '';
  @Input() position: 'top' | 'bottom' | 'left' | 'right' = 'top';
  @Input() bgColor: string = '';
  @Input() textColor: string = '';
  @Input() maxWidth: string = '';

   @HostBinding('style.--tooltip-bg') get tooltipBg() {
    return this.bgColor;
  }

  @HostBinding('style.--tooltip-color') get tooltipColor() {
    return this.textColor;
  }
  constructor(public elRef: ElementRef<HTMLElement>) {}
}

import { Component, Input } from '@angular/core';
import { ChipComponent } from '../../../common-ui/controls/chip/chip.component';


export interface HolidayLeave {
  holidayname: string;
  holidaydate: string;
  holidaylist: 'National Holiday' | 'federal Holiday' | 'Festival Holiday';
}

@Component({
    selector: 'orms-upcoming-holidays',
    templateUrl: './upcoming-holidays.component.html',
    styleUrl: './upcoming-holidays.component.css',
    imports: [ChipComponent]
})
export class UpcomingHolidaysComponent {
  todayDate: Date = new Date();
  @Input() holidayleave: HolidayLeave[] = [];
  

  calculateDays(holidaydate: string, todayDate: Date): number {
    const from = new Date(holidaydate);
    const to = new Date(todayDate || holidaydate);
    return Math.ceil((+from - +to) / 86400000) + 1;
  }
}

import { CommonModule } from '@angular/common';
import { Component, HostListener, Input, OnInit, ViewChild, ElementRef, Renderer2 } from '@angular/core';
import { DropdownValues } from '../../assests/view-models/common-view-models';

@Component({
    selector: 'orms-value-dropdown',
    templateUrl: './value-dropdown.component.html',
    styleUrls: ['./value-dropdown.component.css'],
    imports: [CommonModule]
})
export class ValueDropdownComponent implements OnInit {
  @Input() dropdownValues: DropdownValues[] = [];
  @Input() defaultSelected: string | number | null = null;
  @Input() placeholderText: string = '2024';                      
  @Input() textColor: string = '#111';
  @Input() backgroundColor: string = 'white';
  @Input() activeBackgroundColor: string = '#eaf0ff';
  @Input() borderColor: string = 'lightgray';
  @Input() activeTextColor: string = '#316aff';

  @ViewChild('selectedYearRef', { static: true }) selectedYearRef!: ElementRef<HTMLButtonElement>;
  @ViewChild('ripple', { static: true }) rippleRef!: ElementRef<HTMLSpanElement>;

  selectedYear: string | number | null = null;
  isOpen = false;

  constructor(private renderer: Renderer2) {}

  ngOnInit() {
    this.selectedYear = this.dropdownValues.filter(x=>x.isDefault)[0].value
  }

  toggleDropdown(event: MouseEvent) {
    this.isOpen = !this.isOpen;

    const btn = this.selectedYearRef.nativeElement;
    const ripple = this.rippleRef.nativeElement;

    const rect = btn.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;

    this.renderer.setStyle(ripple, 'top', `${y}px`);
    this.renderer.setStyle(ripple, 'left', `${x}px`);
    this.renderer.removeClass(ripple, 'ripple-active');
    void ripple.offsetWidth;
    this.renderer.addClass(ripple, 'ripple-active');
  }

  selectYear(value: string | number, event: Event) {
    // event.preventDefault();
    this.selectedYear = value;
    this.isOpen = false;
  }

  @HostListener('document:click', ['$event'])
  handleClickOutside(event: MouseEvent) {
    const target = event.target as HTMLElement;
    if (!target.closest('.dropdown')) {
      this.isOpen = false;
    }
  }
}

styleUrlsData.data:
body {
  font-family: "Inter", sans-serif;
  background: #f7f9fc;
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
}

.dropdown {
  position: relative;
  display: inline-block;
}

.dropbtn {
  position: relative;
  overflow: hidden;
  font-size: 16px;
  padding: 10px 25px;
  border: 1px solid lightgray;
  border-radius: 8px;
  cursor: pointer;
  outline: none;
  transition: background-color 0.2s, border-color 0.2s;
}

.dropdown-content {
  display: none;
  position: absolute;
  background-color: #fff;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.08);
  border-radius: 10px;
  z-index: 1;
  overflow: hidden;
  margin-top: 6px;
  min-width: 100%;
  padding: 10px;
}

.dropdown-content button {
  padding: 10px 16px;
  border-radius: 5px;
  text-decoration: none;
  display: block;
  transition: background-color 0.2s, color 0.2s;
}

.dropdown-content button:hover {
  background-color: #f2f4f8 !important;
}

.dropdown-content button.active {
  /* font-weight: 600; */
  background-color: #eaf0ff;
  color: #316aff !important;
}

.show {
  display: block;
}

.ripple {
  position: absolute;
  border-radius: 50%;
  transform: scale(0);
  background-color: rgba(64, 118, 255, 0.35);
  animation: none;
  pointer-events: none;
  width: 20px;
  height: 20px;
}

.ripple-active {
  animation: rippleAnimation 0.6s ease-out;
}

@keyframes rippleAnimation {
  to {
    transform: scale(15);
    opacity: 0;
  }
}

button:disabled {
  opacity: 0.3;
  cursor: not-allowed;
}

