.dob-container {
  display: flex;
  flex-direction: column;
  margin-bottom: 1rem;
  position: relative;

}

.dob-label {
  height: 21px !important;
  font-size: 14px;
  font-weight: 500;
  color: #1E293B !important;
  margin-bottom: 6px;
}

.required-star {
  color: #dc2626;
  font-size: 1rem;
  /* font-weight: 600; */
  /* margin-top: -12px; */
}

.input-container {
  position: relative;
  display: inline-block;
}

.dob-input {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 10px 14px;
  border: 1px solid #d1d5db;
  border-radius: 6px;
  background: #fff;
  cursor: pointer;
  width: 100%;
  box-sizing: border-box;
  transition: all 0.2s ease;
  height: 36px;
  font-size: 12px;
}

.calendar-icon {
  position: absolute;
  right: 10px;
  top: 54%;
  transform: translateY(-50%);
  color: #888;
  pointer-events: none;
  /* ensures clicking icon triggers input */
  transition: color 0.2s ease;
}

.dob-input:focus+.calendar-icon {
  color: #007BFF;
}

.dob-input:focus {
  outline: none;
  border-color: #007BFF;
  box-shadow: 0 0 0 2px rgba(0, 123, 255, 0.25);
  background-color: #f0f8ff;
  transition: all 0.3s ease;
}

/* Calendar Overlay */
.calendar-overlay {
  position:absolute;
  top:100%;
  left:0;
  width:100%;
  overflow-y: auto;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  padding: 0;
  margin-top: 4px; 
  overflow: hidden;
  z-index: 1000;
}

.calendar-container {
  background: #ffffff;
  border-radius: 12px;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.2);
  padding: 16px; /*padding all over*/
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
  width: 100%;
  max-width: inherit; /* Inherit from parent overlay */
  box-sizing: border-box;
}

.calendar-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 8px;
}

.nav-btn {
  background-color:#bababa;
  border: none;
  cursor: pointer;
  padding: 16px;
  margin:8px;
  border-radius: 6px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #333;
  transition: all 0.2s ease;
}

.nav-btn:hover {
  background-color: #dddddd;
  color: #333;
}

.month-year-selectors {
  display: flex;
  gap: 8px;
}

.month-select,
.year-select {
  border: none;
  background: none;
  font-size: 16px;
  font-weight: 500;
  cursor: pointer;
  padding: 4px 8px;
  border-radius: 4px;
  transition: background-color 0.2s ease;
}

.month-select:hover,
.year-select:hover {
  color:#ff6b35;
}
.month-select option:checked,
.year-select option:checked {
  background-color: #ff6b35 !important;
  color: #fff !important;
}
.month-select option,
.year-select option {
  background-color: #fff;
  color: #333;
}
.month-select option:hover,
.year-select option:hover {
  background-color: #ff6b35;
  color: #fff;
}

.month-select:focus,
.year-select:focus {
  outline: none;
  background-color: #e0e0e0;
  color: #ff6b35;
}

.calendar-weekdays {
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  gap: 2px;
}


.calendar-weekdays,
.calendar-grid {
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  justify-items: center;
  align-items: center;
  text-align: center;
  column-gap: 5px; /* space between columns */
  row-gap: 10px;   /* space between rows */
}

.weekday {
  text-align: center;
  font-size: 12px;
  font-weight: 500;
  color: #000000;
  padding: 8px 4px;
}

.calendar-grid {
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  gap: 8px; /* reduce gap between cells */
  padding-bottom: 8px;

}

.calendar-day {
  aspect-ratio: 1;
  width: 50px;            /*decrease cell width */
  height: 50px;          /*decrease cell height */
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  border-radius: 8px;
  font-size: 14px;
  font-weight: 400;
  transition: all 0.2s ease;
  position: relative;
}

.calendar-day.current-month {
  color: #333;
}

.calendar-day.other-month {
  color: #ccc;
}

.calendar-day.current-month:hover {
  background-color: #bcbcbc;
}

.calendar-day.today {
  background-color: #ff6b35;
  color: white;
  font-weight: 500;
}

.calendar-day.selected {
  background-color: #ff6b35;
  color: white;
  font-weight: 500;
  border-radius: 8px;
}

.calendar-day.today.selected {
  background-color: #ff6b35;
  box-shadow: 0 0 0 2px #ff6b35, 0 0 0 4px rgba(255, 107, 53, 0.2);
}
.calendar-footer {
  display: flex;
  justify-content: space-between;
  padding-top: 8px;
  border-top: 1px solid #e0e0e0;
}

.footer-btn {
  background: none;
  border: none;
  cursor: pointer;
  padding: 8px 16px;
  border-radius: 6px;
  font-size: 14px;
  font-weight: 500;
  transition: all 0.2s ease;
}

.today-btn {
  color: #ff6b35;
}

.today-btn:hover {
  background-color: rgba(255, 107, 53, 0.1);
}

.clear-btn {
  color: #666;
}

.clear-btn:hover {
  background-color: #f0f0f0;
}

.close-btn {
  color: #ff6b35;
}

.close-btn:hover {
  background-color: rgba(255, 107, 53, 0.1);
}
.dropdown {
  position: relative;
  width: 120px;
  font-size: 16px;
}

.dropdown-display {
  padding: 6px 10px;
  border-radius: 4px;
  background-color: #fff;
  cursor: pointer;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.arrow {
  font-size: 20px;
  margin-left: 5px;
}

.dropdown-list {
  position: absolute;
  top: 100%;
  left: 0;
  width: 100%;
  max-height: 240px; /* 10 items Ã— ~24px */
  overflow-y: auto;
  background-color: #fff;
  border: 1px solid #ccc;
  border-radius: 4px;
  z-index: 1000;
  box-shadow: 0 4px 8px rgba(0,0,0,0.1);
}

.dropdown-list li {
  padding: 6px 10px;
  cursor: pointer;
  transition: background-color 0.2s ease;
}

.dropdown-list li:hover {
  background-color: #bcbcbc; /* grey on hover */
}

.dropdown-list li.selected {
  background-color: #ff6b35; /* orange when selected */
  color: white;
  font-weight: 500;
}


/* Responsive adjustments */
@media (max-width: 480px) {
  .calendar-overlay {
    padding: 20px;
  }

  .calendar-container {
    width: 100%;
    max-width: 320px;
  }
}

