import { Component, Input, OnInit, OnChanges, SimpleChanges } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-text-box',
  standalone: true,
  templateUrl: './text-box.component.html',
  styleUrls: ['./text-box.component.css'],
  imports: [CommonModule, FormsModule]
})
export class TextBoxComponent implements OnInit, OnChanges {
  @Input() label: string = 'Full Name';
  @Input() placeHolder: string = 'Enter full name';
  @Input() isRequired: boolean = true;
  @Input() showBorder: boolean = true;
  @Input() maxChars: number = 20; // âœ… Change this value â€” it will reflect automatically
  @Input() showCharCount: boolean = true;

  inputText: string = '';
  currentCharCount: number = 0;

  // Initialize with correct value
  ngOnInit() {
    this.resetCharCount();
  }

  // Triggered whenever @Input() maxChars changes
  ngOnChanges(changes: SimpleChanges) {
    if (changes['maxChars']) {
      this.resetCharCount();
    }
  }

  // Called every time user types
  updateCharCount() {
    if (this.inputText.length > this.maxChars) {
      this.inputText = this.inputText.slice(0, this.maxChars);
    }
    this.currentCharCount = this.maxChars - this.inputText.length;
  }

  // Helper to reset count correctly when maxChars changes
  private resetCharCount() {
    this.inputText = ''; // reset text
    this.currentCharCount = this.maxChars; // reset count to new max
  }
}

import { Component, EventEmitter, Input, Output, OnChanges, SimpleChanges } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-toggle',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './toggle.component.html',
  styleUrls: ['./toggle.component.css']
})
export class ToggleComponent implements OnChanges {
  @Input() checked = false;
  @Input() color = '#4CAF50';
  @Input() tickColor: string = '#fff';
  @Output() checkedChange = new EventEmitter<boolean>();
  @Output() toggleChange = new EventEmitter<boolean>();
  @Input() label: string = '';
  @Input() labelPosition: 'left' | 'right' = 'right';
  @Input() labelColor: string = '#333';
  @Input() size: 'large' | 'medium' | 'small' = 'small';
  @Input() useCheckbox: boolean = false;

  toggleWidth = '40px';
  toggleHeight = '20px';
  knobSize = '16px';
  knobTranslate = '20px';

  ngOnChanges(changes: SimpleChanges): void {
    if (changes['size']) this.updateSize();
  }

  ngOnInit(): void {
    this.updateSize();
  }

  updateSize() {
    switch (this.size) {
      case 'large':
        this.toggleWidth = '60px';
        this.toggleHeight = '34px';
        this.knobSize = '26px';
        this.knobTranslate = '26px';
        break;
      case 'medium':
        this.toggleWidth = '44px';
        this.toggleHeight = '24px';
        this.knobSize = '18px';
        this.knobTranslate = '20px';
        break;
      case 'small':
      default:
        this.toggleWidth = '34px';
        this.toggleHeight = '18px';
        this.knobSize = '14px';
        this.knobTranslate = '16px';
        break;
    }
  }

  onToggle(event: Event) {
    const input = event.target as HTMLInputElement;
    this.checked = input.checked;
    this.checkedChange.emit(this.checked);
    this.toggleChange.emit(this.checked);
  }
}

